  $(function() {
    var addUserTag, deleteUserTag;
    var _this = this;
    if ($("#group").length > 0) {
      addUserTag = function(group_id, tag_name, user_name) {
        return $.ajax("/groups/" + group_id + "/user/" + user_name + "/add_user_tag/" + tag_name);
      };
      deleteUserTag = function(group_id, tag_name, user_name) {
        return $.ajax("/groups/" + group_id + "/user/" + user_name + "/delete_user_tag/" + tag_name);
      };
      $("#membership-requested").hover(function(e) {
        return $(this).text("Cancel Request");
      }, function(e) {
        return $(this).text("Membership Requested");
      });
      $("#users-list").children().each(function(index, user_list_element) {
        var available_group_tags, group_id, token_display_element, token_input_object;
        token_display_element = $("#user-tags-" + ($(user_list_element).attr("id")));
        if (token_display_element.length > 0) {
          group_id = $("#group_id").val();
          available_group_tags = "/groups/" + group_id + "/group_tags.json";
          token_input_object = token_display_element.tokenInput(available_group_tags, {
            crossDomain: false,
            preventDuplicates: true,
            theme: "facebook",
            onAdd: function(item) {
              return addUserTag(group_id, item.name, $(user_list_element).attr("id"));
            },
            onDelete: function(item) {
              return deleteUserTag(group_id, item.name, $(user_list_element).attr("id"));
            }
          });
          return $($("#user-existing-tags-" + ($(user_list_element).attr("id"))).val().split(",")).each(function(index, element) {
            if (element !== "") {
              return token_input_object.tokenInput("add", {
                id: element,
                name: element
              });
            }
          });
        }
      });
      if (window.location.href.split("#")[1] === "users") {
        return $(".tabs a:last").click();
      }
    }
  });


