(function() {
  var current_tag_filter, current_tags;

  current_tags = "";

  current_tag_filter = "active";

  $(function() {
    var date, date_string, datetime, datetime_format, display_count, hours, pluralize_characters;
    $(".error-message").hide();
    if ($("#motion-form").length > 0) {
      date = new Date($("#motion_close_date").val());
      date_string = "" + (date.getDate()) + "-" + (date.getMonth() + 1) + "-" + (date.getFullYear());
      hours = date.getHours();
      datetime_format = new Date(date_string);
      $("#input_date").datepicker({
        "dateFormat": "dd-mm-yy"
      });
      $("#input_date").datepicker("setDate", date_string);
      $("#date_hour").val(hours);
      if ($("#new-motion").length > 0) {
        datetime = new Date();
        datetime.setDate(datetime.getDate() + 7);
        hours = datetime.getHours();
        $("#input_date").datepicker({
          "dateFormat": "dd-mm-yy"
        });
        $("#input_date").datepicker("setDate", datetime);
        $("#date_hour").val(hours);
        $("#motion_close_date").val(datetime);
      }
    }
    $(".check-presence").click(function(event, ui) {
      if ($(".presence-required").children().first().val() === "") {
        $(".clearfix").addClass("error");
        $(".error-message").show();
        return false;
      }
    });
    $(".presence-required").keyup(function() {
      $(".clearfix").removeClass("error");
      return $(".error-message").hide();
    });
    $("#input_date").change(function(e) {
      var day, hour, local_datetime, month, year;
      date = $(this).val();
      day = date.substring(0, 2);
      month = (parseInt(date.substring(3, 5)) - 1).toString();
      year = date.substring(6, 10);
      hour = $("#date_hour").val();
      local_datetime = new Date(year, month, day, hour);
      return $("#motion_close_date").val(local_datetime);
    });
    $("#date_hour").change(function(e) {
      var day, hour, local_datetime, month, year;
      date = $("#input_date").val();
      day = date.substring(0, 2);
      month = (parseInt(date.substring(3, 5)) - 1).toString();
      year = date.substring(6, 10);
      hour = $(this).val();
      local_datetime = new Date(year, month, day, hour);
      return $("#motion_close_date").val(local_datetime);
    });
    $(".bordered").click(function(event, ui) {
      var expandableRow, graph_legend;
      expandableRow = $(this).children().last();
      expandableRow.toggle();
      if (expandableRow.is(":visible")) {
        graph_legend = $(this).find(".jqplot-table-legend");
        if ($(this).hasClass('blocked')) {
          return graph_legend.addClass('blocked');
        } else if ($(this).hasClass('voting')) {
          return graph_legend.addClass('voting');
        } else {
          return graph_legend.addClass('closed');
        }
      }
    });
    $(".no-toggle").click(function(event) {
      return event.stopPropagation();
    });
    pluralize_characters = (function(num) {
      if (num === 1) {
        return num + " character";
      } else {
        return num + " characters";
      }
    });
    display_count = (function(num) {
      if (num >= 0) {
        $(".character_counter").text(pluralize_characters(num) + " left");
        return $(".clearfix").removeClass("error");
      } else {
        num = num * (-1);
        $(".character_counter").text(pluralize_characters(num) + " too long");
        return $(".clearfix").addClass("error");
      }
    });
    $(".limited").keyup(function() {
      var chars, left;
      chars = $(".limited").val().length;
      left = 249 - chars;
      return display_count(left);
    });
    $(".vote").click(function(event) {
      if ($(".clearfix").hasClass("error")) {
        return $('#new_vote').preventDefault();
      } else {
        return $('#new_vote').submit();
      }
    });
    $(".limit").keyup(function() {
      var chars, left;
      $(".error-message").hide();
      chars = $(".limit").val().length;
      left = 150 - chars;
      if (left >= 0) {
        $(".character-counter").text(pluralize_characters(left) + " left");
        return $(".clearfix").removeClass("error");
      } else {
        left = left * (-1);
        $(".character-counter").text(pluralize_characters(left) + " too long");
        return $(".clearfix").addClass("error");
      }
    });
    if ($("#motion").length > 0) {
      $("#description").html(linkify_html($("#description").html()));
      return $(".comment-body").each(function() {
        return $(this).html(linkify_html($(this).html()));
      });
    }
  });

}).call(this);




